var navMain = document.querySelector('.main-nav');
var navToggle = document.querySelector('.main-nav__toggle');

navMain.classList.remove('main-nav--nojs');

navToggle.addEventListener('click', function() {
  if (navMain.classList.contains('main-nav--closed')) {
    navMain.classList.remove('main-nav--closed');
    navMain.classList.add('main-nav--opened');
  }
  else {
    navMain.classList.remove('main-nav--opened');
    navMain.classList.add('main-nav--closed');
  }
});
  document.addEventListener('click', ({ target: t }) => {
  if (t.classList.contains('like__img')) {
    const index = [...document.querySelectorAll('.like__img')].indexOf(t);
    const count = document.querySelectorAll('.like__number')[index];
    count.classList.toggle('active');
    count.textContent -= [ 1, -1 ][+count.classList.contains('active')];
  }
});



